namespace App\Console\Commands\CategoryTree;

class Tajrobi extends GetTree
{
    public function getLastUpdatedByLernito(): array
    {
        $tajrobiLernitoStyle = $this->getLernitoStyle();
        $lernitoTotalTree = new lernitoTotalTree();
        $lernitoTree = $lernitoTotalTree->getTree();
        $lernitoTree = $lernitoTree[1]['children'][1]['children'];

        return $this->compareWithLernito($lernitoTree, $tajrobiLernitoStyle);
    }

    public function getLernitoStyle()
    {
        /*return $this->treeToLernitoJson($this->getTree());*/
        $tree = $this->getTree();
        $tajrobiLernitoStyle = [
            $tree[1],
            $tree[2],
            $tree[3],
            $tree[0]['children'][0],
            $tree[0]['children'][1],
            $tree[0]['children'][2],
            $tree[0]['children'][3],
        ];

        return $tajrobiLernitoStyle;
    }

